mapper.select:
   		select q_id as qid,content
	from (select row_number() over (order by q_id_sq desc) num, A.* from tbl_q A order by q_id_sq desc) 
	where num between #{startrow} and #{startrow}+10 and r_cnt>10


select answer_id as answerId,content, i_record_id as iRecordId,r_cnt as rCnt, regdate
 from (select row_number() over (order by regdate desc) num, A.* from tbl_answer A order by regdate desc) 
where q_id=#{q_id} and num between #{startrow} and #{startrow}+10


select answer_id as answerId,content, i_record_id as iRecordId,r_cnt as rCnt, regdate
 from (select row_number() over (order by r_cnt desc) num, A.* from tbl_answer A order by r_cnt desc) 
where q_id=#{q_id} and num between #{startrow} and #{startrow}+10
